'use client'

import BlockKitEditor from '@/components/Editor'
import { Editor, JSONContent } from '@tiptap/core'
import { PageDescription, TiptapPageContent } from '@/lib/client/types'
import {
  TargetAction,
  useCommandMenu
} from '@/components/Chrome/CommandMenuContext'
import { useCallback, useEffect, useRef, useState } from 'react'
import { deletePage } from '@/app/(main)/workspaces/[workspaceSlug]/pages/actions'
import { useRouter } from 'next/navigation'
import { useSearchParams } from 'next/navigation'

export default function PageEditor({
  page,
  content
}: {
  page: PageDescription
  content: TiptapPageContent
}) {
  // Make the current title available for onSave to compare against
  // document title changes.
  // useState is not a good fit here because the previous page title
  // is the last one closed over so that when document changes occur
  // after the last title change, it always appears the title has changed.
  const titleRef = useRef<string>(page.title)

  const [deleted, setDeleted] = useState(false)
  const router = useRouter()
  const commandMenu = useCommandMenu()
  const searchParams = useSearchParams()
  const focus: boolean = searchParams.get('focus') === 'true'

  useEffect(() => {
    const deletePageAction: TargetAction = {
      actionId: 'page-delete',
      target: page,
      onSelect: async (p) => {
        if (confirm('Are you sure you want to delete this page?')) {
          setDeleted(true)
          await deletePage(p.id)
          router.push(`/workspaces/${page.workspace.slug}/pages`)
        }
      }
    }

    commandMenu.activate(deletePageAction)
    return () => commandMenu.deactivate(deletePageAction)
  }, [router, page, commandMenu])

  const onSave = useCallback(
    async (editor: Editor) => {
      if (deleted) return
      try {
        let newTitle: string | undefined = undefined

        const json = editor.getJSON()
        const heading = json.content?.length ? json.content?.[0] : null
        if (heading?.type === 'heading') {
          newTitle = heading.content?.[0].text
        }
        await fetch('/api/v1/page-content', {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json'
          },
          body: JSON.stringify({
            id: content.id,
            tiptapContent: JSON.stringify(json)
          })
        })
        if (newTitle && newTitle !== titleRef.current) {
          titleRef.current = newTitle
          router.refresh()
        }
      } catch (error) {
        console.log(error)
      }
    },
    [router, deleted, content.id, titleRef]
  )

  return (
    <div className="mx-auto my-[60px] max-w-[800px]">
      <BlockKitEditor
        content={(content.tiptapContent as JSONContent) ?? ''}
        onSave={onSave}
        autofocus={focus}
        workspaceId={page.workspace.id}
      />
    </div>
  )
}
