```ts taskpage/components/Editor/Mentions/suggestion.ts
import { ReactRenderer } from '@tiptap/react'
import tippy, {
  Instance as TippyInstance,
  GetReferenceClientRect
} from 'tippy.js'
import MentionList, { MentionListHandle, MentionListProps } from './MentionList'
import {
  SuggestionOptions,
  SuggestionProps,
  SuggestionKeyDownProps
} from '@tiptap/suggestion'
import { searchPages } from '@/app/(main)/workspaces/[workspaceSlug]/pages/actions'

const suggestion: Omit<SuggestionOptions<string>, 'editor'> = {
  items: async ({
    query,
    workspaceId
  }: {
    query: string
    workspaceId: number
  }) => {
    const results = await searchPages(query, workspaceId)
    return results.map((page) => page.title)
  },

  render: () => {
    let component: ReactRenderer<MentionListHandle, MentionListProps>
    let popup: TippyInstance[]

    return {
      onStart: (props: SuggestionProps<string>) => {
        component = new ReactRenderer(MentionList, {
          props,
          editor: props.editor
        })

        if (!props.clientRect) {
          return
        }

        popup = tippy('body', {
          getReferenceClientRect: props.clientRect as GetReferenceClientRect,
          appendTo: () => document.body,
          content: component.element,
          showOnCreate: true,
          interactive: true,
          trigger: 'manual',
          placement: 'bottom-start'
        })
      },

      onUpdate(props: SuggestionProps<string>) {
        component.updateProps(props)

        if (!props.clientRect) {
          return
        }

        popup[0].setProps({
          getReferenceClientRect: props.clientRect as GetReferenceClientRect
        })
      },

      onKeyDown(props: SuggestionKeyDownProps) {
        if (props.event.key === 'Escape') {
          popup[0].hide()

          return true
        }

        return component.ref?.onKeyDown(props) || false
      },

      onExit() {
        popup[0].destroy()
        component.destroy()
      }
    }
  }
}

export default suggestion
