'use server'

import { prisma } from '@/lib/database'
import { revalidatePath } from 'next/cache'
import { getAuthenticatedSessionUser } from '@/lib/auth'
import { Page, User, Workspace } from '@prisma/client'
import { createPage } from '@/lib/models/pages'

export async function addPage(workspaceId: number): Promise<Page> {
  const user = await getAuthenticatedSessionUser()
  const workspace = await prisma.workspace.findUniqueOrThrow({
    select: { id: true, slug: true },
    where: { id: workspaceId, users: { some: { userId: user.id } } }
  })
  const page = await createPage(workspace.id, user.id, {
    title: 'New Page'
  })
  revalidatePath('/pages')
  revalidatePath(`/workspaces/${workspace.slug}/pages`)
  return page
}

export async function deletePage(pageId: number): Promise<void> {
  const user = await getAuthenticatedSessionUser()
  const page = await prisma.page.delete({
    where: {
      id: pageId,
      OR: [
        { users: { some: { userId: user.id } } },
        { workspace: { users: { some: { userId: user.id } } } }
      ]
    },
    select: { workspace: { select: { slug: true } } }
  })
  revalidatePath('/pages')
  revalidatePath(`/workspaces/${page.workspace.slug}/pages`)
}

export async function getWorkspacePages(
  workspaceId: Workspace['id'],
  userId: User['id']
): Promise<Page[]> {
  return prisma.page.findMany({
    where: { workspaceId, workspace: { users: { some: { userId } } } },
    orderBy: [{ createdAt: 'desc' }]
  })
}

export async function searchPages(
  searchTerm: string,
  workspaceId: number
): Promise<Page[]> {
  const user = await getAuthenticatedSessionUser()

  // Ensure user has access to the workspace
  const workspaceAccess = await prisma.workspace.findUnique({
    where: {
      id: workspaceId,
      users: {
        some: {
          userId: user.id
        }
      }
    }
  })

  if (!workspaceAccess) {
    throw new Error('Access denied or workspace not found.')
  }

  const pages = await prisma.page.findMany({
    where: {
      workspaceId,
      title: {
        contains: searchTerm,
        mode: 'insensitive'
      }
    },
    orderBy: {
      createdAt: 'desc'
    },
    take: 20 // Limit results for performance
  })

  return pages.length ? pages : []
}
