```ts taskpage/components/Editor/Mentions/suggestion.ts
import { ReactRenderer } from '@tiptap/react'
import tippy, {
  Instance as TippyInstance,
  GetReferenceClientRect
} from 'tippy.js'
import MentionList, { MentionListHandle, MentionListProps } from './MentionList'
import {
  SuggestionOptions,
  SuggestionProps,
  SuggestionKeyDownProps
} from '@tiptap/suggestion'
import { searchPages } from '@/app/(main)/workspaces/[workspaceSlug]/pages/actions'
import debounceFn from 'debounce-fn'
import { PageDescription } from '@/lib/models/pages'

const debouncedSearchPages = debounceFn(
  async (
    query: string,
    workspaceId: number,
    callback: (pages: PageDescription[]) => void
  ) => {
    const results = await searchPages(query, workspaceId)
    callback(results)
  },
  { wait: 300 }
)

type MentionSuggestionOptions = Omit<
  SuggestionOptions<PageDescription>,
  'editor' | 'items'
> & {
  items: (input: {
    query: string
    workspaceId: number
  }) => Promise<PageDescription[]>
}

const suggestion: MentionSuggestionOptions = {
  items: async ({
    query,
    workspaceId
  }: {
    query: string
    workspaceId: number
  }) => {
    return new Promise<PageDescription[]>((resolve) => {
      debouncedSearchPages(query, workspaceId, resolve)
    })
  },

  render: () => {
    let component: ReactRenderer<MentionListHandle, MentionListProps>
    let popup: TippyInstance[]

    return {
      onStart: (props: SuggestionProps<PageDescription>) => {
        component = new ReactRenderer(MentionList, {
          props,
          editor: props.editor
        })

        if (!props.clientRect) {
          return
        }

        popup = tippy('body', {
          getReferenceClientRect: props.clientRect as GetReferenceClientRect,
          appendTo: () => document.body,
          content: component.element,
          showOnCreate: true,
          interactive: true,
          trigger: 'manual',
          placement: 'bottom-start'
        })
      },

      onUpdate(props: SuggestionProps<PageDescription>) {
        component.updateProps(props)

        if (!props.clientRect) {
          return
        }

        popup[0].setProps({
          getReferenceClientRect: props.clientRect as GetReferenceClientRect
        })
      },

      onKeyDown(props: SuggestionKeyDownProps) {
        if (props.event.key === 'Escape') {
          popup[0].hide()

          return true
        }

        return component.ref?.onKeyDown(props) || false
      },

      onExit() {
        popup?.[0].destroy()
        component?.destroy()
      }
    }
  }
}

export default suggestion
```


