        component = new ReactRenderer(MentionList, {
          props: {
            ...props,
            onSelect: (item) => {  // Handle item selection
              // Insert a mention with additional attributes
              props.editor
                .chain()
                .focus()
                .insertContentAt(props.range, {
                  type: 'mention',
                  attrs: {
